SELECT A.COB_DATE, a.account, a.book, a.ccc_product_line, a.ccc_division, a.ccc_strategy, CASE WHEN a.book LIKE ('%ARM%') THEN 'FIXED' ELSE 'FLOAT' END AS MTG_RATE_TYPE, CASE WHEN ACCOUNT IN ('083003ZU0', '083003ZY2', '083004FN6') OR ACCOUNT LIKE ('%083003ZZ%') OR BOOK IN ( 'TFUTR' , 'TOPTN' , 'EFUTR' , 'EOPTN' ) THEN 'HFI Mortgage Structural Hedges' else a.MSPBNA_MORTGAGE_TYPE end as mspbna_mortgage_type, null AS MSPBNA_MORTGAGE_SIZE, a.PO_IO_STATUS, SUM (COALESCE (a.USD_IR_KAPPA, 0) ::numeric(15,5) / 10) AS USD_KAPPA, SUM (COALESCE (a.USD_IR_UNIFIED_PV01, 0)::numeric(15,5)) AS USD_PV01, SUM (COALESCE (a.USD_PV01SPRD, 0)::numeric(15,5)) AS USD_PV01SPRD, SUM (COALESCE (a.USD_NOTIONAL, 0)::numeric(15,5)) AS USD_NOTIONAL, SUM (COALESCE (a.USD_MARKET_VALUE, 0)::numeric(15,5)) AS USD_MARKET_VALUE, SUM (a.SLIDE_IR_MIN_100BP_USD) ::numeric(15,5) AS SLIDE_IR_MIN_100BP_USD, SUM (a.SLIDE_IR_PLS_50BP_USD) ::numeric(15,5) AS SLIDE_IR_PLS_50BP_USD, SUM (a.SLIDE_IR_PLS_100BP_USD) ::numeric(15,5) AS SLIDE_IR_PLS_100BP_USD, SUM (a.SLIDE_IR_PLS_200BP_USD) ::numeric(15,5) AS SLIDE_IR_PLS_200BP_USD, SUM (a.SLIDE_IR_PLS_300BP_USD) ::numeric(15,5) AS SLIDE_IR_PLS_300BP_USD, SUM (a.USD_EXPOSURE) ::numeric(15,5) AS USD_EXPOSURE FROM cdwuser.U_DM_WM A WHERE A.COB_DATE IN ( '2018-02-28', '2018-01-31', '2017-12-29', '2017-11-30', '2017-10-31', '2017-09-29', '2013-12-31' ) and a.CCC_TAPS_COMPANY = '6635' AND (a.var_excl_fl <> 'Y' OR a.BOOK IN ('MSDPB3M', 'CRASW', 'CCISW')) AND (a.ccc_banking_trading = 'BANKING' OR (a.CCC_HIERARCHY_LEVEL2 IN ('WEALTH MANAGEMENT', 'GLOBAL WEALTH MANAGEMENT') AND a.PRODUCT_TYPE_CODE = 'REPO' AND a.CCC_BUSINESS_AREA NOT IN ('NON CORE MARKETS', 'CORE MARKETS'))) AND (CCC_PRODUCT_LINE = 'HELD FOR INVESTMENT - (HFI)' OR CCC_PRODUCT_LINE IN ('HELD FOR INVESTMENT - (HFI)', 'HELD FOR SALE - (HFS)', 'FIXED INCOME DIVISION', 'NON-AGENCY TRADING') OR (CCC_PRODUCT_LINE = 'HELD FOR SALE - (HFS)' OR BOOK = 'CCISW') OR CCC_STRATEGY = 'RESI WM JV') AND A.CCC_BANKING_TRADING = 'BANKING' GROUP BY A.COB_DATE, a.account, a.book, a.ccc_product_line, a.ccc_division, a.ccc_strategy, CASE WHEN a.book LIKE ('%ARM%') THEN 'FIXED' ELSE 'FLOAT' END, CASE WHEN ACCOUNT IN ( '083003ZU0' , '083003ZY2' , '083004FN6' ) OR ACCOUNT LIKE ('%083003ZZ%') OR BOOK IN ( 'TFUTR' , 'TOPTN' , 'EFUTR' , 'EOPTN' ) THEN 'HFI Mortgage Structural Hedges' else a.MSPBNA_MORTGAGE_TYPE end, a.po_io_status