SELECT A.COB_DATE, A.WM_BANKING_PRODUCT_GROUP, A.WM_BANKING_PRODUCT_SUB_GROUP, A.PRODUCT_HIERARCHY_LEVEL7, A.TERM_BUCKET, A.TERM_BUCKET_AGGR, SUM((A.USD_NOTIONAL) :: numeric(15,5)) AS USD_NOTIONAL, SUM((A.USD_MARKET_VALUE) :: numeric(15,5)) AS USD_MARKET_VALUE, SUM((A.USD_IR_UNIFIED_PV01) :: numeric(15,5)) AS USD_PV01, sum((A.SLIDE_IR_PLS_200BP_USD) :: numeric(15,5)) as SLIDE_IR_PLS_200BP_USD, sum((A.SLIDE_IR_PLS_100BP_USD) :: numeric(15,5)) as SLIDE_IR_PLS_100BP_USD FROM CDWUSER.U_DM_WM A WHERE A.COB_DATE in ( '2018-02-28', '2018-01-31' ) and (A.CCC_STRATEGY in ('AVAILABLE FOR SALE','HFI HEDGES') OR A.CCC_STRATEGY='INVESTMENT PORTFOLIO') AND (A.PRODUCT_HIERARCHY_LEVEL7='IR_SWAPTION_EUROPEAN' and A.book not in ('CSSW2', 'CSSWA') or A.PRODUCT_HIERARCHY_LEVEL7='IR_SWAP') and a.CCC_DIVISION IN ('PRIVATE BANKING GROUP', 'RETAIL BANKING GROUP', 'PWM US BRANCH') GROUP BY A.COB_DATE, A.WM_BANKING_PRODUCT_GROUP, A.WM_BANKING_PRODUCT_SUB_GROUP, A.PRODUCT_HIERARCHY_LEVEL7, A.TERM_BUCKET, A.TERM_BUCKET_AGGR