SELECT A.COB_DATE, A.WM_BANKING_PRODUCT_GROUP, A.WM_BANKING_PRODUCT_SUB_GROUP, A.PRODUCT_HIERARCHY_LEVEL7, case when term_bucket in ('0-0.25Y', '0.25-0.5Y', '0.5-1Y', '1-2Y') then '0-2Y' when term_bucket in ('2-3Y', '3-5Y', '2-5Y') then '2-5Y' when term_bucket in ('5-7Y') then '5-7Y' ELSE '>7Y' end as term_bucket, SUM((A.USD_IR_UNIFIED_PV01) :: numeric(15,5)) AS USD_PV01 FROM CDWUSER.U_DM_WM A WHERE A.COB_DATE in ( '2018-02-28', '2018-01-31', '2017-12-29', '2017-11-30', '2017-10-31', '2017-09-29' ) and (A.CCC_STRATEGY in ('AVAILABLE FOR SALE','HFI HEDGES') OR A.CCC_STRATEGY='INVESTMENT PORTFOLIO') AND (A.PRODUCT_HIERARCHY_LEVEL7='IR_SWAPTION_EUROPEAN' and A.book not in ('CSSW2', 'CSSWA') or A.PRODUCT_HIERARCHY_LEVEL7='IR_SWAP') and a.CCC_DIVISION IN ('PRIVATE BANKING GROUP', 'RETAIL BANKING GROUP', 'PWM US BRANCH') GROUP BY A.COB_DATE, A.WM_BANKING_PRODUCT_GROUP, A.WM_BANKING_PRODUCT_SUB_GROUP, A.PRODUCT_HIERARCHY_LEVEL7, case when term_bucket in ('0-0.25Y', '0.25-0.5Y', '0.5-1Y', '1-2Y') then '0-2Y' when term_bucket in ('2-3Y', '3-5Y', '2-5Y') then '2-5Y' when term_bucket in ('5-7Y') then '5-7Y' ELSE '>7Y' end