SELECT a.COB_DATE, a.CCC_TAPS_COMPANY, a.CCC_BUSINESS_AREA, a.CCC_PRODUCT_LINE, a.BOOK, a.ACCOUNT, CASE WHEN A.BOOK IN ('PBCDP', 'RPOCX') THEN 'Other Liabilities' ELSE a.WM_BANKING_PRODUCT_GROUP END AS WM_BANKING_PRODUCT_GROUP, CASE WHEN A.BOOK IN ('PBCDP') THEN 'Brokered CD' WHEN A.BOOK IN ('RPOCX') THEN 'FHLB Program' ELSE a.WM_BANKING_PRODUCT_SUB_GROUP END AS WM_BANKING_PRODUCT_SUB_GROUP, Case when a.WM_BANKING_PRODUCT_SUB_GROUP like '%AGN%' then 'Agency' when a.WM_BANKING_PRODUCT_SUB_GROUP like '%Credit%' then 'Credit' when a.WM_BANKING_PRODUCT_SUB_GROUP like '%Govt%' then 'Treasury' else 'Other' end as Group, SUM (USD_NOTIONAL) AS USD_NOTIONAL, SUM (USD_EXPOSURE) AS USD_EXPOSURE, SUM (MV) AS MV, SUM (USD_PV01SPRD) AS USD_PV01SPRD, SUM (USD_PV01) AS USD_PV01, SUM (USD_IR_KAPPA) AS USD_IR_KAPPA FROM ( SELECT a.POSITION_ID, a.COB_DATE, a.CCC_TAPS_COMPANY, a.CCC_BUSINESS_AREA, a.CCC_PRODUCT_LINE, a.VERTICAL_SYSTEM, a.BOOK, a.ACCOUNT, a.PRODUCT_TYPE_CODE, a.WM_BANKING_PRODUCT_GROUP, a.WM_BANKING_PRODUCT_SUB_GROUP, a.BANKING_PRODUCT_DESCRIPTION, a.PRODUCT_SUB_TYPE_CODE, a.PRODUCT_TYPE_NAME, SUM (COALESCE (((USD_TOTAL_COMMIT) :: numeric(15,5)), 0)) / 1000 AS USD_TOTAL_COMMIT, SUM (COALESCE (((USD_UNFUNDED_COMMIT) :: numeric(15,5)), 0)) / 1000 AS USD_UNFUNDED_COMMIT, SUM (COALESCE (((USD_NOTIONAL) :: numeric(15,5)), 0)) / 1000 AS USD_NOTIONAL, SUM (COALESCE (((USD_EXPOSURE) :: numeric(15,5)), 0)) / 1000 AS USD_EXPOSURE, SUM (COALESCE (((USD_PROCEED) :: numeric(15,5)), 0)) / 1000 AS MV, SUM (COALESCE (((USD_PV01SPRD) :: numeric(15,5)), 0)) / 1000 AS USD_PV01SPRD, SUM (COALESCE (((USD_IR_KAPPA) :: numeric(15,5)), 0) / 10) AS USD_IR_KAPPA, SUM (COALESCE (((USD_CONVX) :: numeric(15,5)), 0) / 1000) AS USD_CONVX, SUM (COALESCE (((USD_IR_UNIFIED_PV01) :: numeric(15,5)), 0)) AS USD_PV01 FROM cdwuser.u_dm_wm_position a WHERE A.COB_DATE IN ( '2018-02-28' ) AND (A.VAR_EXCL_FL <> 'Y' OR A.BOOK IN ('MSDPB3M', 'MSDPT3M')) AND (A.CCC_HIERARCHY_LEVEL2 IN ('WEALTH MANAGEMENT', 'GLOBAL WEALTH MANAGEMENT') OR a.WM_BANKING_PRODUCT_SUB_GROUP IN ('Cash')) GROUP BY a.POSITION_ID, a.COB_DATE, a.CCC_TAPS_COMPANY, a.CCC_BUSINESS_AREA, a.CCC_PRODUCT_LINE, a.VERTICAL_SYSTEM, a.BOOK, a.ACCOUNT, a.PRODUCT_TYPE_CODE, a.PRODUCT_SUB_TYPE_CODE, a.WM_BANKING_PRODUCT_GROUP, a.WM_BANKING_PRODUCT_SUB_GROUP, a.BANKING_PRODUCT_DESCRIPTION, a.PRODUCT_TYPE_NAME ) A GROUP BY a.COB_DATE, a.CCC_TAPS_COMPANY, a.CCC_BUSINESS_AREA, a.CCC_PRODUCT_LINE, a.BOOK, a.ACCOUNT, CASE WHEN A.BOOK IN ('PBCDP', 'RPOCX') THEN 'Other Liabilities' ELSE a.WM_BANKING_PRODUCT_GROUP END, CASE WHEN A.BOOK IN ('PBCDP') THEN 'Brokered CD' WHEN A.BOOK IN ('RPOCX') THEN 'FHLB Program' ELSE a.WM_BANKING_PRODUCT_SUB_GROUP END, Case when a.WM_BANKING_PRODUCT_SUB_GROUP like '%AGN%' then 'Agency' when a.WM_BANKING_PRODUCT_SUB_GROUP like '%Credit%' then 'Credit' when a.WM_BANKING_PRODUCT_SUB_GROUP like '%Govt%' then 'Treasury' else 'Other' end