SELECT A.COB_DATE, A.VAL_IN, A.VAL_FOR, SUM((A.GRID_USD_KAPPA) :: numeric(15,5)) AS KAPPA FROM CDWUSER.U_DM_WM A WHERE A.COB_DATE in ( '2018-02-28', '2018-01-31' ) and (A.CCC_STRATEGY in ('AVAILABLE FOR SALE','HFI HEDGES') OR A.CCC_STRATEGY='INVESTMENT PORTFOLIO') AND (A.PRODUCT_HIERARCHY_LEVEL7='IR_SWAPTION_EUROPEAN' and A.book not in ('CSSW2', 'CSSWA') or A.PRODUCT_HIERARCHY_LEVEL7='IR_SWAP') AND PARENT_LEGAL_ENTITY In ('6635(G)') and a.CCC_DIVISION IN ('PRIVATE BANKING GROUP', 'RETAIL BANKING GROUP', 'PWM US BRANCH') GROUP BY A.COB_DATE, A.VAL_IN, A.VAL_FOR