SELECT SUM (usd_exposure) AS net_exposure, SUM (USD_PV01SPRD) AS SPV01, a.COB_DATE, a.COUNTRY_CD_OF_RISK FROM cdwuser.U_CR_MSR a WHERE a.ccc_business_area IN ('SECURITIZED PRODUCTS GRP') AND a.COUNTRY_CD_OF_RISK IN ('ITA', 'PRT', 'IRL', 'GRC', 'ESP') AND a.ccc_pl_reporting_region IN ('EMEA') AND a.COB_DATE in ('2018-02-28','2018-02-21','2018-01-31','2017-12-29','2017-11-30','2017-09-29') and a.SPG_DESC not in('CORPORATE INDEX','CORPORATE DEFAULT SWAP','CORPORATE CLO','CMBS LOAN', 'WAREHOUSE ABS LOAN') group by a.COB_DATE, a.COUNTRY_CD_OF_RISK