SELECT SUM (USD_FX) AS FX, cob_date, currency_of_measure, vertical_system, CCC_PRODUCT_LINE, CCC_STRATEGY, ccc_business_area, a.ccc_division FROM cdwuser.U_FX_MSR a where cob_date in ('2018-02-28') AND a.ccc_division IN ('FIXED INCOME DIVISION', 'INSTITUTIONAL EQUITY DIVISION') AND usd_fx <> 0 AND currency_of_measure IN ('SAR') AND ccc_banking_trading_localreg = 'TRADING' AND ccc_business_area NOT IN ('CPM', 'DSP - CREDIT', 'LENDING', 'OTC CLIENT CLEARING') GROUP BY cob_date, currency_of_measure, CCC_STRATEGY, CCC_PRODUCT_LINE, vertical_system, ccc_business_area, a.ccc_division