Select COB_DATE, UK_CLASSIFICATION, sum(net_exposure)as net_exposure FROM ( SELECT a.PRODUCT_DESCRIPTION, a.COB_DATE, spg_desc, SUM (usd_exposure) AS net_exposure, (CASE WHEN a.product_description LIKE ('PUNCH%') THEN 'Pubs' WHEN a.product_description LIKE ('%TESCO PROPERTY%') OR a.product_description LIKE ('%TSCOLN%') THEN 'Tesco' WHEN spg_desc IN ('RMBS ALTA SECURITY', 'RMBS CDO', 'RMBS NON CONFORMING DEFAULT SWAP', 'RMBS SECOND RESIDUAL', 'RMBS SUB PRIME RESIDUAL', 'RMBS SUB PRIME SECURITY') THEN 'RMBS NC' WHEN spg_desc LIKE 'CMBS%' OR PRODUCT_DESCRIPTION LIKE ('TSCOLN%') THEN 'CMBS' WHEN spg_desc IN ('RMBS PRIME DEFAULT SWAP', 'RMBS PRIME RESIDUAL', 'RMBS PRIME SECURITY', 'RMBS ALTA RESIDUAL', 'RMBS DEFAULT SWAP') THEN 'RMBS PRIME' WHEN spg_desc IN ('ABS AUTO LOAN & SECURITY', 'ABS CREDIT CARD SECURITY', 'ABS DEFAULT SWAP', 'ABS EQUIPMENT SECURITY', 'ABS OTHER SECURITY', 'ABS STUDENT SECURITY', 'ABS CREDIT BASKET', 'CORPORATE BONDS') OR PRODUCT_DESCRIPTION LIKE ('JUNEAU%') THEN 'ABS' END) AS UK_classification FROM cdwuser.U_CR_MSR a WHERE CCC_business_area IN ('SECURITIZED PRODUCTS GRP') AND COUNTRY_CD_OF_RISK IN ('GBR') AND a.COB_DATE in ('2018-02-28','2018-02-21') AND a.CCC_PRODUCT_LINE NOT IN ('CRE LENDING SEC/HFS','CREL BANK HFI') AND a.CCC_PL_REPORTING_REGION IN('EMEA') GROUP BY a.PRODUCT_DESCRIPTION, a.COB_DATE, spg_desc ) x WHERE UK_CLASSIFICATION in ('ABS','CMBS','Pubs','RMBS NC','RMBS PRIME','Tesco') group by COB_DATE, UK_CLASSIFICATION