SELECT CASE WHEN CURRENCY_OF_MEASURE IN ('BRX', 'BR1') THEN 'BRL' WHEN CURRENCY_OF_MEASURE = 'KRX' THEN 'KRW' WHEN CURRENCY_OF_MEASURE IN ( 'RU1' , 'RBX') THEN 'RUB' ELSE CURRENCY_OF_MEASURE END AS CURRENCY_OF_MEASURE, CASE WHEN a.CCC_TAPS_COMPANY = '0302' THEN 'MSIP' WHEN a.CCC_TAPS_COMPANY = '0111' THEN 'MSCS' ELSE 'Other' END AS WHICH_LE, SUM (a.USD_FX) AS usd_fx FROM cdwuser.U_FX_MSR a where cob_date in ('2018-02-28') AND a.ccc_division = 'FIXED INCOME DIVISION' AND ccc_business_area NOT IN ('CPM') AND a.currency_of_measure IN ('CNH', 'CNY', 'HKD', 'INR', 'IDR', 'KRW', 'KRX', 'MOP', 'MYR', 'PHP', 'SGD', 'TWD', 'THB', 'MNT', 'PKR', 'VND', 'LKR', 'PGK', 'HKD') AND a.USD_FX <> 0 AND a.USD_FX IS NOT NULL GROUP BY a.currency_of_measure, CASE WHEN a.CCC_TAPS_COMPANY = '0302' THEN 'MSIP' WHEN a.CCC_TAPS_COMPANY = '0111' THEN 'MSCS' ELSE 'Other' END