SELECT abs(SUM (USD_IR_UNIFIED_PV01)) AS usd_pv01, cob_date, a.TAPSCUSIP FROM DWUSER.U_IR_MSR a where cob_date between '2017-02-28' AND '2018-02-28' AND (CCC_BUSINESS_AREA IN ('INTEREST RATES', 'LIQUID FLOW RATES', 'STRUCTURED RATES')) AND VAR_EXCL_FL = 'N' AND VERTICAL_SYSTEM NOT IN ('PIPELINE_NY') AND ccc_product_line NOT LIKE 'MS CVA MPE%' AND ccc_business_area NOT LIKE 'CPM TRADING%' AND (ccc_product_line NOT LIKE 'NON IG PRIMARY%' OR ccc_strategy NOT LIKE 'NON IG PRIMARY%') AND (ccc_product_line NOT IN ('MS CVA MPE DERIVS CPM', 'CORE MPE CVA', 'MONOLINE MPE CVA', 'MS CVA MNE DERIVS IRCC') OR ccc_strategy NOT IN ('MS CVA MPE DERIVS CPM', 'CORE MPE CVA', 'MONOLINE MPE CVA', 'MS CVA MNE DERIVS IRCC')) AND CCC_BUSINESS_AREA NOT IN ('LENDING') AND CCC_BUSINESS_AREA NOT LIKE '%CVA%' AND a.CCC_PRODUCT_LINE = 'EU GOVERNMENTS' AND a.currency_of_measure = 'EUR' and A.USD_IR_UNIFIED_PV01 IS NOT NULL GROUP BY cob_date, a.TAPSCUSIP