SELECT cob_date,A.CCC_PL_REPORTING_REGION,LE_GROUP, Case when A.FID1_SENIORITY in ('AT1','SUBT1','SUBUT2') then 'Junior Sub' when A.CCC_PRODUCT_LINE in ('DISTRESSED TRADING') then 'Distressed' when (A.CCC_BUSINESS_AREA ='CREDIT-CORPORATES' and A.MRD_RATING IN ('AAA', 'AA', 'A', 'BBB')) then 'INV GRADE' when (A.CCC_BUSINESS_AREA ='CREDIT-CORPORATES' and A.MRD_RATING not in ('AAA', 'AA', 'A', 'BBB')) then 'HIGH YIELD' when A.CCC_BUSINESS_AREA in ('LIQUID FLOW RATES','STRUCTURED RATES') Then 'INTEREST RATES' when A.CCC_BUSINESS_AREA in ('FXEM MACRO TRADING','EM CREDIT TRADING') Then 'FXEM' when A.CCC_BUSINESS_AREA in ('CPM') Then 'CVA' when A.CCC_BUSINESS_AREA in ('DSP - CREDIT','CREDIT-CORPORATES','MUNICIPAL SECURITIES','SECURITIZED PRODUCTS GRP') Then 'Other Credit' else a.CCC_DIVISION end as BUS, sum(coalesce(a.USD_PV10_Bench,0)+coalesce(a.USD_Credit_PV10PCT,0)) AS bpv10 FROM cdwuser.u_cr_msr a WHERE a.COB_DATE in ('2018-02-28','2018-02-21','2017-12-29','2017-09-29') AND LE_GROUP ='UK' AND A.CCC_BANKING_TRADING ='TRADING' GROUP BY cob_date,A.CCC_PL_REPORTING_REGION,LE_GROUP, Case when A.FID1_SENIORITY in ('AT1','SUBT1','SUBUT2') then 'Junior Sub' when A.CCC_PRODUCT_LINE in ('DISTRESSED TRADING') then 'Distressed' when (A.CCC_BUSINESS_AREA ='CREDIT-CORPORATES' and A.MRD_RATING IN ('AAA', 'AA', 'A', 'BBB')) then 'INV GRADE' when (A.CCC_BUSINESS_AREA ='CREDIT-CORPORATES' and A.MRD_RATING not in ('AAA', 'AA', 'A', 'BBB')) then 'HIGH YIELD' when A.CCC_BUSINESS_AREA in ('LIQUID FLOW RATES','STRUCTURED RATES') Then 'INTEREST RATES' when A.CCC_BUSINESS_AREA in ('FXEM MACRO TRADING','EM CREDIT TRADING') Then 'FXEM' when A.CCC_BUSINESS_AREA in ('CPM') Then 'CVA' when A.CCC_BUSINESS_AREA in ('DSP - CREDIT','CREDIT-CORPORATES','MUNICIPAL SECURITIES','SECURITIZED PRODUCTS GRP') Then 'Other Credit' else a.CCC_DIVISION end