SELECT COB_DATE, SUM (USD_EXPOSURE::numeric(15,5)) AS USD_EXPOSURE FROM CDWUSER.U_CR_MSR WHERE ccc_division = 'TREASURY CAPITAL MARKETS' AND cob_date in ('2018-02-28', '2018-02-21') AND PRODUCT_HIERARCHY_LEVEL7 = 'MONEY MARKET MUTUAL FUNDS' AND CCC_PL_REPORTING_REGION = 'ASIA PACIFIC' GROUP BY COB_DATE