SELECT * FROM ( SELECT 'EMEA' AS CUT, a.COB_DATE, a.CCC_BUSINESS_AREA, a.CCC_PRODUCT_LINE, a.CCC_TAPS_COMPANY, a.PRODUCT_TYPE_CODE, CASE WHEN a.CURRENCY_OF_MEASURE NOT IN ('EUR', 'GBP', 'USD') THEN 'OTHERS' ELSE a.CURRENCY_OF_MEASURE END AS CURRENCY_GROUP, a.CURRENCY_OF_MEASURE, a.GICS_LEVEL_1_NAME, a.GICS_LEVEL_2_NAME, CASE WHEN (GICS_LEVEL_1_NAME = 'FINANCIALS' OR (GICS_LEVEL_1_NAME IS NULL AND FID1_INDUSTRY_NAME_LEVEL1='CORPORATES: FINANCIALS')) then 'FINANCIALS' else 'NON-FINANCIALS' end as FLAG_INDUSTRY, CASE WHEN a.FID1_SENIORITY IN ('AT1', 'SUBT1', 'SUBUT2') THEN 'JUNIOR' ELSE 'SENIOR' END AS FLAG_SENIORITY, CASE WHEN a.MRD_RATING IN ('AAA', 'AA', 'A', 'BBB') THEN 'IG' ELSE 'HY' END AS FLAG_INVESTMENT_GRADE, CASE WHEN a.PRODUCT_TYPE_CODE IN ('CASH', 'BOND', 'BANKDEBT') THEN 'CASH' ELSE 'DERIVATIVES' END AS FLAG_CASH_DERIVATIVES, a.FID1_SENIORITY, a.MRD_RATING, CASE WHEN a.ISSUER_COUNTRY_CODE IN ('ALB','AND','AUT','BLR','BEL','BIH','BGR','CYP','HRV','CZE','DNK','EST','FRO','FIN','FRA','GEO','DEU','GRC','GGY','VAT','HUN','ISL','IRL','IMN','ITA','JEY','LVA','LIE','LTU','LUX','MKD','MLT','MDA','MCO','MNE','NLD','NOR','POL','PRT','ROU','RUS','SMR','SRB','SVK','SVN','ESP','SJM','SWE','CHE','UKR') THEN 'Europe' WHEN a.ISSUER_COUNTRY_CODE IN ('GBR') THEN 'UK' WHEN a.ISSUER_COUNTRY_CODE IN ('USA','CAN') THEN 'NAM' WHEN a.ISSUER_COUNTRY_CODE IN ('AIA','ATG','ARG','ABW','BHS','BRB','BLZ','BMU','BOL','BRA','VGB','CYM','CHL','COL','CRI','CUB','DMA','DOM','ECU','SLV','GUF','GRL','GRD','GLP','GTM','GUY','HTI','HND','JAM','MTQ','MEX','MSR','ANT','NIC','PAN','PRY','PER','PRI','KNA','LCA','SPM','VCT','SGS','SUR','TTO','TCA','URY','VEN','VIR') THEN 'LATAM' WHEN a.ISSUER_COUNTRY_CODE IN ('DZA','AGO','BEN','BWA','BFA','BDI','CMR','CPV','CAF','TCD','COM','COD','CIV','DJI','EGY','GNQ','ERI','ETH','GAB','GMB','GHA','GIB','GIN','GNB','KEN','LSO','LBR','LBY','MDG','MWI','MLI','MRT','MUS','MYT','MAR','MOZ','NAM','NER','NGA','REU','RWA','BLM','SHN','MAF','STP','SEN','SYC','SLE','SOM','ZAF','SSD','SDN','SWZ','TZA','TGO','TUN','UGA','ESH','ZMB','ZWE') THEN 'Africa' WHEN a.ISSUER_COUNTRY_CODE IN ('AFG','ALA','ARM','AZE','BHR','BGD','BTN','IOT','BRN','KHM','CHN','HKG','CXR','CCK','FLK','GUM','IND','IDN','IRN','IRQ','ISR','JPN','JOR','KAZ','PRK','KOR','KWT','KGZ','LAO','LBN','MYS','MDV','MNG','MMR','NPL','OMN','PAK','PSE','PHL','QAT','SAU','SGP','LKA','SYR','TWN','TJK','THA','TUR','TKM','ARE','UMI','UZB','VNM','YEM') THEN 'Asia' WHEN a.ISSUER_COUNTRY_CODE IN ('ASM','ATA','AUS','BVT','COK','FJI','PYF','ATF','HMD','KIR','MHL','FSM','NRU','NCL','NZL','NIU','NFK','MNP','PLW','PNG','PCN','WSM','SLB','TLS','TKL','TON','TUV','VUT','WLF') THEN 'Pacific' ELSE 'Other' END AS FLAG_REGION, a.ISSUER_COUNTRY_CODE, a.POSITION_ULT_ISSUER_PARTY_DARWIN_NAME, SUM (COALESCE (a.USD_IR_UNIFIED_PV01,0)) AS USD_PV01, SUM (COALESCE (a.USD_PV01SPRD,0)) AS USD_PV01SPRD, SUM (COALESCE (a.USD_PV10_BENCH,0)) AS USD_PV10_BENCH, SUM (COALESCE (a.USD_EXPOSURE,0)) AS USD_EXPOSURE FROM cdwuser.U_EXP_MSR a WHERE a.COB_DATE in ('2018-02-28','2018-02-21','2018-01-31','2017-12-29','2017-11-30','2017-09-29') AND a.CCC_DIVISION = 'FIXED INCOME DIVISION' AND a.CCC_BANKING_TRADING = 'TRADING' AND a.CCC_BUSINESS_AREA IN ('CREDIT-CORPORATES') AND a.CCC_PRODUCT_LINE not in ('DISTRESSED TRADING', 'PRIMARY - LOANS', 'PAR LOANS TRADING') AND a.CCC_PL_REPORTING_REGION = 'EMEA' GROUP BY a.COB_DATE, a.CCC_BUSINESS_AREA, a.CCC_PRODUCT_LINE, a.CCC_TAPS_COMPANY, a.PRODUCT_TYPE_CODE, CASE WHEN a.CURRENCY_OF_MEASURE NOT IN ('EUR', 'GBP', 'USD') THEN 'OTHERS' ELSE a.CURRENCY_OF_MEASURE END, a.CURRENCY_OF_MEASURE, a.GICS_LEVEL_1_NAME, a.GICS_LEVEL_2_NAME, CASE WHEN (GICS_LEVEL_1_NAME = 'FINANCIALS' OR (GICS_LEVEL_1_NAME IS NULL AND FID1_INDUSTRY_NAME_LEVEL1='CORPORATES: FINANCIALS')) then 'FINANCIALS' else 'NON-FINANCIALS' end, a.FID1_SENIORITY, a.MRD_RATING, a.ISSUER_COUNTRY_CODE, a.POSITION_ULT_ISSUER_PARTY_DARWIN_NAME ) x WHERE 1=1 AND FLAG_SENIORITY='SENIOR'