SELECT A.COB_DATE, A.CCC_BUSINESS_AREA, case when A.PRODUCT_SUB_TYPE_CODE in ('N EAST CONSUMPTI', 'N EAST CONSUMPTION') then 'N EAST CONSUMPTI' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('N EAST APPALACHI', 'N EAST APPALACHIA') THEN 'N EAST APPALACHI' ELSE A.PRODUCT_SUB_TYPE_CODE END AS PRODUCT_SUB_TYPE_CODE, case when A.PRODUCT_TYPE_CODE in ('PRECIOUSMETAL', 'FUND') then 'PRECIOUSMETAL' when A.PRODUCT_TYPE_CODE in ('BASEMETAL') then 'BASEMETAL' when A.PRODUCT_TYPE_CODE in ('COAL','COAL-US',/*'DRY FREIGHT',*/'EMISSIONS-EU','IRON ORE') then 'BULKS' end as PRODUCT_TYPE_CODE, A.CMDTY_CD, SUM((A.RAW_CM_DELTA)) as RAW_DELTA, SUM((A.USD_CM_DELTA)) AS DOLLAR_DELTA, SUM((A.RAW_CM_KAPPA)) as RAW_VEGA, SUM((A.USD_CM_KAPPA)) AS DOLLAR_VEGA, case when A.PROD_POS_NAME_DESCRIPTION in ('GLD', 'SLV') then 'ETF' else 'Physical' end as ETF_FLAG, PROD_POS_NAME_DESCRIPTION FROM cdwuser.U_CM_MSR A WHERE ((A.CCC_DIVISION='COMMODITIES' AND A.CCC_BUSINESS_AREA IN ('METALS')) /*OLG LOGIC*/ OR (A.CCC_DIVISION = 'FIXED INCOME DIVISION' AND A.CCC_BUSINESS_AREA = 'COMMODITIES' and (a.CCC_PRODUCT_LINE IN ('PRECIOUS METALS') OR A.CCC_STRATEGY IN ('BASE METALS', 'BULKS')))) /*NEW LOGIC*/ AND A.COB_DATE IN ('2018-02-28','2018-01-31','2017-12-29','2017-09-29','2017-06-30','2017-03-31') AND A.PRODUCT_TYPE_CODE NOT IN ('CURRENCY', 'INTEREST RATE', 'INFLATION', 'TBD', 'MISC','CVA', 'FVA', 'ERROR') GROUP BY A.COB_DATE, A.CCC_BUSINESS_AREA, A.PRODUCT_SUB_TYPE_CODE, A.PRODUCT_TYPE_CODE, A.CMDTY_CD, PROD_POS_NAME_DESCRIPTION