SELECT a.COB_DATE, Case when (EXTRACT (year FROM a.EXPIRATION_DATE) >= 2019 and EXTRACT (year FROM a.EXPIRATION_DATE) <= 2024) then '2019-2024' when EXTRACT (year FROM a.EXPIRATION_DATE) > 2024 then '>2024' else cast(EXTRACT (year FROM a.EXPIRATION_DATE) as char(4)) end as YEAR, SUM(a.USD_CM_Delta) AS DOLLAR_DELTA, case when product_type_code in ('EAST OFF', 'EAST PEAK', 'MIDWEST OFF', 'MIDWEST PEAK', 'TEXAS OFF', 'TEXAS PEAK', 'WEST OFF', 'WEST PEAK','GRNPWR OFF','GRNPWR PEAK','EAST INTERCONNECT OF','EAST INTERCONNECT PE','TEXAS INTERCONNECT O', 'TEXAS INTERCONNECT P', 'ERCOT', 'WEST INTERCONNECT OF', 'WEST INTERCONNECT PE') then 'Power' when product_type_code in ('NATGAS') then 'NatGas' else 'OTHER' end as POWERGAS FROM cdwuser.U_CM_MSR a WHERE A.COB_DATE IN ('2018-02-28','2018-01-31','2017-12-29','2017-09-29','2017-06-30','2017-03-31') AND ((a.CCC_DIVISION='COMMODITIES' AND a.CCC_BUSINESS_AREA = 'NA ELECTRICITYNATURAL GAS') or (a.CCC_DIVISION = 'FIXED INCOME DIVISION' AND A.CCC_BUSINESS_AREA = 'COMMODITIES' AND A.CCC_PRODUCT_LINE IN ('NA POWER & GAS'))) and NOT(a.INCLUDE_IN_REG_CAAP_FL = 'N' and a.PRODUCT_SUB_TYPE_CODE in ('N EAST CONSUMPTI', 'N EAST CONSUMPTION', 'N EAST APPALACHI', 'N EAST APPALACHIA') and a.BOOK = '18003') AND A.PRODUCT_TYPE_CODE NOT IN ('CURRENCY', 'INTEREST RATE', 'INFLATION', 'TBD', 'MISC','CVA', 'FVA', 'ERROR') GROUP BY a.COB_DATE, a.EXPIRATION_DATE, a.PRODUCT_TYPE_CODE