SELECT COB_DATE, case WHEN ACCOUNT IN ('07700A931') THEN 'Defunded Assets' ELSE A.BOOK_STRATEGY END AS BOOK_STRATEGY, sum(coalesce(CASE WHEN book in('SWPIN','TDET6', 'TAIRR', 'TAEMR', 'TEEMR', 'HKFAP', 'TTIRR', 'HKFAM', 'TKFVS', 'HKFAS', 'TBAGR') THEN -usd_notional WHEN (book in('CALLT', 'CALTB', 'LCALT', 'LCATB')) THEN usd_MARKET_VALUE else usd_notional END, 0)) as usd_notional, SUM (COALESCE (usd_market_value,0)) AS usd_market_value FROM cdwuser.u_dm_treasury a WHERE cob_date >= '2017-11-29' AND ccc_division = 'TREASURY CAPITAL MARKETS' and LE_GROUP = 'UK' and BOOK not in ('TSYGLRCSH') GROUP BY COB_DATE, case WHEN ACCOUNT IN ('07700A931') THEN 'Defunded Assets' ELSE A.BOOK_STRATEGY END