select a.COB_DATE, a.CURRENCY_OF_MEASURE, CASE WHEN a.CURRENCY_MAPPING IN ('USD', 'GBP', 'EUR', 'JPY') THEN A.CURRENCY_MAPPING ELSE 'OTHER' END AS CURRENCY, a.MTM_BOOK_POPULATION, a.BOOK, a.BUCKETED_TERM, Case WHEN var_excl_fl ='Y' THEN 'Excluded' ELSE 'Included' END AS Var_Inclusion, sum(coalesce(USD_IR_UNIFIED_PV01,0)) as USD_IR_UNIFIED_PV01 from cdwuser.U_DM_TREASURY a where CCC_DIVISION = 'TREASURY CAPITAL MARKETS' and cob_date in ('2018-02-28', '2018-02-27') and a.BOOK not in ('TSYGLRCSH') and (MTM_BOOK_POPULATION = 'MTM BOOKS' or BOOK = 'TSTOL') group by a.COB_DATE, a.CURRENCY_OF_MEASURE, a.MTM_BOOK_POPULATION, CASE WHEN a.CURRENCY_MAPPING IN ('USD', 'GBP', 'EUR', 'JPY') THEN A.CURRENCY_MAPPING ELSE 'OTHER' END, Case WHEN var_excl_fl ='Y' THEN 'Excluded' ELSE 'Included' END, a.BOOK, a.BUCKETED_TERM