select COB_DATE, book, case when BOOK in ('RPOTO', 'RPOAF', 'RPOBK', 'RPOSE') or account in ('07000AF31') then 'NY' when BOOK in ('RWUIP','RWUIS', 'NYDI','RWUUK','RWULT', 'RWPO3') or account in ('07600AGA8') THEN 'LN' when BOOK in ('RWILX') THEN 'AP' else 'OTHER BOOKS' end as BOOK_REGION, sum(a.USD_IR_UNIFIED_PV01) as pv01 from cdwuser.U_DM_TREASURY a where cob_date in ('2018-02-28', '2018-02-21') and CCC_DIVISION = 'TREASURY CAPITAL MARKETS' and CCC_BUSINESS_AREA = 'LIQUIDITY RESERVE1' and PRODUCT_TYPE_CODE = 'REPO' AND (BOOK = 'RPOTO' OR (TERM_OF_POSITION/365 > 0.019 AND ((BOOK not in ('EULT') and account in ('07000AF31')) or book not in ('LX','EUG7','EUC5','NYN1','NYN2','RPOCO','RPOTU'))) OR BOOK in ('RWUIS', 'NYDI','RWUIP', 'RWULT','RWUUK') ) and CCC_PRODUCT_LINE = 'LIQUIDITY' and BOOK not in ('TSYGLRCSH') group by COB_DATE, case when BOOK in ('RPOTO', 'RPOAF', 'RPOBK', 'RPOSE') or account in ('07000AF31') then 'NY' when BOOK in ('RWUIP','RWUIS', 'NYDI','RWUUK','RWULT', 'RWPO3') or account in ('07600AGA8') THEN 'LN' when BOOK in ('RWILX') THEN 'AP' else 'OTHER BOOKS' end, BOOK