SELECT COB_DATE, 'METALS' AS CCC_BUSINESS_AREA, SUM(USD_CM_Delta) AS DOLLAR_DELTA, SUM(RAW_CM_Delta) AS RAW_DELTA FROM CDWUSER.U_CM_MSR a WHERE (CCC_DIVISION='COMMODITIES' OR (CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA = 'COMMODITIES')) AND (CCC_BUSINESS_AREA = ('METALS') OR (CCC_PRODUCT_LINE = 'PRECIOUS METALS' OR (CCC_PRODUCT_LINE ='COMMOD LEGACY TRADING' AND CCC_STRATEGY IN ('BULKS','BASE METALS')))) AND PRODUCT_TYPE_CODE NOT IN ('CURRENCY', 'INTEREST RATE', 'INFLATION', 'TBD', 'MISC','CVA', 'FVA','FUND','CLEAN FREIGHT', 'DIRTY FREIGHT') AND BOOK NOT IN ('Y10425', 'Y10426') AND COB_DATE IN ('2018-02-28','2018-02-27','2018-01-31','2017-12-29','2017-09-29','2017-06-30') GROUP BY COB_DATE, CCC_BUSINESS_AREA UNION SELECT COB_DATE, 'OIL LIQUIDS' AS CCC_BUSINESS_AREA, SUM(USD_CM_Delta) AS DOLLAR_DELTA, SUM(RAW_CM_Delta) AS RAW_DELTA FROM CDWUSER.U_CM_MSR WHERE (CCC_DIVISION='COMMODITIES' OR (CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA = 'COMMODITIES')) AND (CCC_BUSINESS_AREA IN ('OIL LIQUIDS', 'TMG', 'OLYMPUS') OR (CCC_PRODUCT_LINE = 'OIL & PRODUCTS' AND CCC_STRATEGY = 'EMEA OIL & PRODUCTS')) AND PRODUCT_TYPE_CODE NOT IN ('CURRENCY', 'INTEREST RATE', 'INFLATION', 'TBD', 'MISC','CVA', 'FVA', 'CLEAN FREIGHT', 'DIRTY FREIGHT') AND COB_DATE IN ('2018-02-28','2018-02-27','2018-01-31','2017-12-29','2017-09-29','2017-06-30') AND CCC_PL_REPORTING_REGION = 'EMEA' GROUP BY COB_DATE, CCC_BUSINESS_AREA