SELECT COB_DATE, SUM (USD_EXPOSURE) AS USD_EXPOSURE FROM cdwuser.U_dm_treasury a WHERE ccc_division = 'TREASURY CAPITAL MARKETS' AND cob_date in ('2018-02-28', '2018-02-27', '2018-01-31', '2017-12-29', '2017-11-30', '2017-10-31','2017-09-29','2017-08-31', '2017-12-29') AND PRODUCT_HIERARCHY_LEVEL7 = 'MONEY MARKET MUTUAL FUNDS' AND CCC_PL_REPORTING_REGION = 'ASIA PACIFIC' and BOOK not in ('TSYGLRCSH') GROUP BY COB_DATE