SELECT TIME_BUCKET_ANNUAL||'.'||TIME_BUCKET_QUARTER AS EXP_DATE_QUARTER, COB_DATE, SUM (RAW_CM_KAPPA) AS RAW_VEGA FROM CDWUSER.U_CM_MSR A WHERE (CCC_DIVISION='COMMODITIES' OR (CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA = 'COMMODITIES')) AND CCC_PRODUCT_LINE = 'PRECIOUS METALS' AND CCC_BUSINESS_AREA NOT IN ('MS CVA MNE - COMMOD') AND /*CCC_PL_REPORTING_REGION = 'EMEA' AND*/ /*CCC_TRD_BOOK <> 'DEVELOPMENT' AND*/ TIME_BUCKET_ANNUAL NOT LIKE '%UNDEFINED%' AND COB_DATE BETWEEN ('2018-01-29') AND ('2018-02-28') AND PRODUCT_TYPE_CODE NOT IN ( 'INTEREST RATE', 'INFLATION', 'TBD', 'MISC', 'CVA', 'FVA', 'CURRENCY','FUND','CLEAN FREIGHT', 'DIRTY FREIGHT') AND BOOK NOT IN ('Y10425', 'Y10426') GROUP BY EXP_DATE_QUARTER, COB_DATE