select a.COB_DATE, A.CURRENCY_OF_MEASURE, sum(coalesce(USD_IR_UNIFIED_PV01,0)) as PV01, abs(sum(coalesce(usd_IR_UNIFIED_PV01,0))) as ABS_PV01 from cdwuser.U_dm_treasury a where CCC_DIVISION = 'TREASURY CAPITAL MARKETS' and cob_date in ('2018-02-28', '2018-02-21', '2018-01-31', '2017-12-29', '2017-11-30', '2017-10-31','2017-09-29','2017-08-31', '2017-12-29') and a.BOOK not in ('TSYGLRCSH') AND a.CURRENCY_OF_MEASURE NOT IN ('USD', 'GBP', 'EUR', 'JPY') and (MTM_BOOK_POPULATION = 'MTM BOOKS' or BOOK = 'TSTOL') group by a.COB_DATE, A.CURRENCY_OF_MEASURE