SELECT COB_DATE, PRODUCT_TYPE_CODE, SUM (RAW_CM_DELTA) AS RAW_DELTA FROM CDWUSER.U_CM_MSR WHERE COB_DATE IN ('2018-02-27', '2018-02-28') AND (CCC_DIVISION='COMMODITIES' OR (CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA = 'COMMODITIES')) AND (CCC_BUSINESS_AREA IN ('OIL LIQUIDS', 'TMG', 'OLYMPUS') OR (CCC_PRODUCT_LINE = 'OIL & PRODUCTS' AND CCC_STRATEGY = 'EMEA OIL & PRODUCTS')) AND CCC_PL_REPORTING_REGION = 'EMEA' GROUP BY COB_DATE, PRODUCT_TYPE_CODE