SELECT a.cob_date, a.PRODUCT_TYPE_CODE, CASE WHEN A.PRODUCT_SUB_TYPE_CODE IN ('N EAST CONSUMPTI', 'N EAST CONSUMPTION') THEN 'N EAST CONSUMPTION' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('N EAST APPALACHI', 'N EAST APPALACHIA') THEN 'N EAST APPALACHIA' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('POLYPROPELENE HO', 'POLYPROPELENE HOMOPOLYMER') THEN 'POLYPROPELENE HOMOPOLYMER' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('POLYPROPYLENE CO', 'POLYPROPYLENE COPOLYMER') THEN 'POLYPROPYLENE COPOLYMER' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('PURE TEREPHTHALI', 'PURE TEREPHTHALIC ACID') THEN 'PURE TEREPHTHALIC ACID' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('GSCI-PRECIOUS ME', 'GSCI-PRECIOUS METALS') THEN 'GSCI-PRECIOUS METALS' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('ZERO PRICED EXPO', 'ZERO PRICED EXPOSURE') THEN 'ZERO PRICED EXPOSURE' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('DJUBS-BASE METAL', 'DJUBS-BASE METALS') THEN 'DJUBS-BASE METALS' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('GAS SULFUR CREDI', 'GAS SULFUR CREDIT') THEN 'GAS SULFUR CREDIT' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('GASOIL TIMESPREA', 'GASOIL TIMESPREAD') THEN 'GASOIL TIMESPREAD' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('USD INTEREST RAT', 'USD INTEREST RATE') THEN 'USD INTEREST RATE' ELSE A.PRODUCT_SUB_TYPE_CODE END AS PRODUCT_SUB_TYPE_CODE, a.PROD_POS_NAME_DESCRIPTION, a.CCC_STRATEGY_DETAILS, a.PRODUCT_TYPE_NAME, a.BU_RISK_SYSTEM, a.USD_CM_DELTA, a.POSITION_EXPIRY_MONTH, a.POSITION_EXPIRY_YEAR FROM CDWUSER.U_CM_MSR a WHERE a.cob_date IN ('2018-02-28', '2018-02-27') AND ((a.CCC_DIVISION = 'COMMODITIES' AND a.CCC_BUSINESS_AREA = 'INVESTOR BUSINESS' AND a.CCC_STRATEGY_DETAILS = 'TRADING') /*OLD COMMOD LOGIC*/ OR (a.CCC_DIVISION = 'FIXED INCOME DIVISION' AND A.CCC_BUSINESS_AREA = 'COMMODITIES' AND a.CCC_PRODUCT_LINE IN ('COMMOD EXOTICS', 'COMMOD INDEX' ) AND a.CCC_STRATEGY = 'TRADING')/*NEW COMMOD LOGIC*/ ) AND a.PRODUCT_TYPE_CODE not in ('GSCI') AND a.BU_RISK_SYSTEM in ('NY-INDEX-SWAP', 'NY-INDEX-WASH', 'BMINDEXTRADING')