SELECT CASE WHEN a.CCC_Division NOT IN ('FIXED INCOME DIVISION', 'INSTITUTIONAL EQUITY DIVISION', 'COMMODITIES') THEN 'Others' WHEN CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA = 'COMMODITIES' THEN 'COMMODITIES' ELSE a.CCC_Division END AS CCC_Division, CCC_BANKING_TRADING, SUM (COALESCE (a.USD_EQ_DELTA_DECOMP,0)) AS NRAMV, 0 AS USD_EQ_KAPPA FROM cdwuser.U_DECOMP_MSR a WHERE a.cob_date IN ('2018-02-28') AND a.LE_GROUP = 'UK' AND CCC_DIVISION NOT IN ('FID DVA','FIC DVA') AND CCC_STRATEGY NOT IN ('MS DVA STR NOTES IED') GROUP BY CASE WHEN a.CCC_Division NOT IN ('FIXED INCOME DIVISION', 'INSTITUTIONAL EQUITY DIVISION', 'COMMODITIES') THEN 'Others' WHEN CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA = 'COMMODITIES' THEN 'COMMODITIES' ELSE a.CCC_Division END, CCC_BANKING_TRADING UNION ALL SELECT CASE WHEN a.CCC_Division NOT IN ('FIXED INCOME DIVISION', 'INSTITUTIONAL EQUITY DIVISION', 'COMMODITIES') THEN 'Others' WHEN CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA = 'COMMODITIES' THEN 'COMMODITIES' ELSE a.CCC_Division END AS CCC_Division, CCC_BANKING_TRADING, 0 AS NRAMV, SUM(coalesce(a.USD_EQ_PARTIAL_KAPPA,0)) AS USD_EQ_KAPPA FROM cdwuser.U_EQ_MSR a WHERE a.cob_date IN ('2018-02-28') AND a.LE_GROUP = 'UK' AND CCC_DIVISION NOT IN ('FID DVA','FIC DVA') AND CCC_STRATEGY NOT IN ('MS DVA STR NOTES IED') GROUP BY CASE WHEN a.CCC_Division NOT IN ('FIXED INCOME DIVISION', 'INSTITUTIONAL EQUITY DIVISION', 'COMMODITIES') THEN 'Others' WHEN CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA = 'COMMODITIES' THEN 'COMMODITIES' ELSE a.CCC_Division END, CCC_BANKING_TRADING FOR READ ONLY