SELECT CCC_BANKING_TRADING AS CCC_BANKING_TRADING, CASE WHEN a.CCC_Division IN ('BANK RESOURCE MANAGEMENT','FIXED INCOME DIVISION','NON CORE','TREASURY CAPITAL MARKETS') THEN 'FID+BRM' ELSE a.CCC_Division END AS CCC_Division, SUM(a.USD_EQ_KAPPA_PLS100BP_TIMEADJ) AS TA_Kappa, SUM(a.USD_THETA_GAMMA) AS USD_Theta_Gamma, SUM(COALESCE(a.USD_EQ_DISC_RISK, 0)) AS div01, SUM(a.CORR_EQEQ) AS CORR_EQEQ FROM cdwuser.U_DM_EQ a WHERE a.cob_date IN ('2018-02-28') AND a.CCC_BUSINESS_AREA NOT IN ('LENDING') AND IS_UK_GROUP = 'Y' AND CCC_DIVISION NOT IN ('FID DVA','FIC DVA') AND CCC_STRATEGY NOT IN ('MS DVA STR NOTES IED') GROUP BY CCC_BANKING_TRADING, CASE WHEN a.CCC_Division IN ('BANK RESOURCE MANAGEMENT','FIXED INCOME DIVISION','NON CORE','TREASURY CAPITAL MARKETS') THEN 'FID+BRM' ELSE a.CCC_Division END