SELECT CASE WHEN CURRENCY_CODE IN ('USD', 'EUR', 'GBP', 'JPY') THEN CURRENCY_CODE WHEN CURRENCY_CODE IN ('AUD','CAD','CHF','DKK','NOK','NZD','SEK') THEN 'OthersMajor' ELSE 'OthersEM' END AS CURRENCY_CODE1, a.VAL_FOR, SUM(GRID_MEASURE_VALUE/10) as GRID_MEASURE_VALUE FROM cdwuser.U_GRID_MSR a WHERE a.cob_date IN ('2018-02-28') AND LE_GROUP = 'UK' AND GRID_MEASURE_NAME in ('USD_KAPPA') AND CCC_DIVISION NOT IN ('FID DVA','FIC DVA') AND CCC_STRATEGY NOT IN ('MS DVA STR NOTES IED') GROUP BY a.VAL_FOR, CURRENCY_CODE, CASE WHEN CURRENCY_CODE IN ('USD', 'EUR', 'GBP', 'JPY') THEN CURRENCY_CODE WHEN CURRENCY_CODE IN ('AUD','CAD','CHF','DKK','NOK','NZD','SEK') THEN 'OthersMajor' ELSE 'OthersEM' END