SELECT RANK () OVER (ORDER BY ABS(SUM(a.USD_EQ_DELTA_DECOMP) ) DESC) || '_TopUKNRAMVinclPB' AS CONCENTRATION, CASE WHEN a.PRODUCT_DESCRIPTION_DECOMP IS NULL THEN 'UNDEFINED' ELSE a.PRODUCT_DESCRIPTION_DECOMP END PRODUCT_DESCRIPTION_DECOMP, SUM(a.USD_EQ_DELTA_DECOMP) AS USD_EQ_DELTA_DECOMP FROM cdwuser.U_DECOMP_MSR a WHERE a.cob_date IN ('2018-02-28') AND a.USD_EQ_DELTA_DECOMP IS NOT NULL AND a.CCC_DIVISION = 'INSTITUTIONAL EQUITY DIVISION' AND a.CCC_BANKING_TRADING = 'TRADING' AND a.LE_GROUP = 'UK' AND a.CCC_BUSINESS_AREA NOT IN ('LENDING') AND CCC_DIVISION NOT IN ('FID DVA','FIC DVA') AND CCC_STRATEGY NOT IN ('MS DVA STR NOTES IED') GROUP BY a.PRODUCT_DESCRIPTION_DECOMP ORDER BY ABS(SUM(a.USD_EQ_DELTA_DECOMP)) DESC FETCH FIRST 10 ROWS ONLY