SELECT SUM(ABS(vega)) AS Grossvega FROM ( SELECT UNDERLIER_TICK || '.' || UNDERLIER_EXCH AS Underlier, SUM(COALESCE(a.USD_EQ_PARTIAL_KAPPA,0)) AS vega FROM cdwuser.U_EQ_MSR a WHERE a.cob_date IN ('2018-02-28') AND a.LE_GROUP = 'UK' AND a.EXECUTIVE_MODEL IN ('VARIANCESWAP','CAPPEDVARIANCESWAP','VOLATILITYSWAP','CAPPEDVOLATILITYSWAP'/*,'CONDITIONAL-VARIANCE-SPREAD-TEMPLATE','CONDITIONAL-VARSWAP-VS-CONDITIONAL-VARSWAP-TEMPLATE','CONDITIONAL-VARSWAP-VS-VARSWAP-TEMPLATE','CONDITIONAL-VARSWAP-VS-VARSWAP-TWO-ASSETS-TEMPLATE','KNOCKIN-VAR-SWAP-TEMPLATE','REALIZED-VARIANCE-VS-REPLICATION-TEMPLATE','BASKET-VARIANCE-SWAP-TEMPLATE','CONDITIONAL-VARSWAP-PDE-TEMPLATE','KNOCKOUT-VARSWAP-PDE-TEMPLATE','CONDITIONAL-VARSWAP-VS-CONDITIONAL-VARSWAP-OUTPERF-TEMPLATE','VARIANCESWAPONVOLTARGET'*/) GROUP BY UNDERLIER_TICK || '.' || UNDERLIER_EXCH ) abc