SELECT SUM (COALESCE (SLIDE_FXOPT_MIN_20PCT_USD, 0)) AS neg20, SUM (COALESCE (SLIDE_FXOPT_MIN_10PCT_USD, 0)) AS neg10, SUM (COALESCE (SLIDE_FXOPT_MIN_05PCT_USD, 0)) AS neg5, SUM (COALESCE (SLIDE_FXOPT_MIN_03PCT_USD, 0)) AS neg3, SUM (COALESCE (SLIDE_FXOPT_MIN_01PCT_USD, 0)) AS neg1, SUM (COALESCE (SLIDE_FXOPT_MIN_0P5PCT_USD, 0)) AS neg0p5, SUM (COALESCE (SLIDE_FXOPT_PLS_0P5PCT_USD, 0)) AS pos0p5, SUM (COALESCE (SLIDE_FXOPT_PLS_01PCT_USD,0)) AS pos1, SUM (COALESCE (SLIDE_FXOPT_PLS_03PCT_USD,0)) AS pos3, SUM (COALESCE (SLIDE_FXOPT_PLS_05PCT_USD,0)) AS pos5, SUM (COALESCE (SLIDE_FXOPT_PLS_10PCT_USD,0)) AS pos10, SUM (COALESCE (SLIDE_FXOPT_PLS_20PCT_USD,0)) AS pos20 FROM ( SELECT CCC_BUSINESS_AREA, CCC_PRODUCT_LINE, CCC_DIVISION, CURRENCY_PAIR, SUM (COALESCE (SLIDE_FXOPT_MIN_20PCT_USD, 0)) AS SLIDE_FXOPT_MIN_20PCT_USD, SUM (COALESCE (SLIDE_FXOPT_MIN_10PCT_USD, 0)) AS SLIDE_FXOPT_MIN_10PCT_USD, SUM (COALESCE (SLIDE_FXOPT_MIN_05PCT_USD, 0)) AS SLIDE_FXOPT_MIN_05PCT_USD, SUM (COALESCE (SLIDE_FXOPT_MIN_03PCT_USD, 0)) AS SLIDE_FXOPT_MIN_03PCT_USD, SUM (COALESCE (SLIDE_FXOPT_MIN_01PCT_USD, 0)) AS SLIDE_FXOPT_MIN_01PCT_USD, SUM (COALESCE (SLIDE_FXOPT_MIN_0P5PCT_USD, 0)) AS SLIDE_FXOPT_MIN_0P5PCT_USD, 0 AS slide0, SUM (COALESCE (SLIDE_FXOPT_PLS_0P5PCT_USD, 0)) AS SLIDE_FXOPT_PLS_0P5PCT_USD, SUM (COALESCE (SLIDE_FXOPT_PLS_01PCT_USD, 0)) AS SLIDE_FXOPT_PLS_01PCT_USD, SUM (COALESCE (SLIDE_FXOPT_PLS_03PCT_USD, 0)) AS SLIDE_FXOPT_PLS_03PCT_USD, SUM (COALESCE (SLIDE_FXOPT_PLS_05PCT_USD, 0)) AS SLIDE_FXOPT_PLS_05PCT_USD, SUM (COALESCE (SLIDE_FXOPT_PLS_10PCT_USD, 0)) AS SLIDE_FXOPT_PLS_10PCT_USD, SUM (COALESCE (SLIDE_FXOPT_PLS_20PCT_USD, 0)) AS SLIDE_FXOPT_PLS_20PCT_USD FROM cdwuser.U_DM_FX f WHERE f.cob_date IN ('2018-02-28') AND (currency_pair LIKE '%USD%' OR currency_pair LIKE '%UBD%') AND (currency_pair NOT LIKE '%CNY%' OR currency_pair NOT LIKE '%CNH%' OR currency_pair NOT LIKE '%HKD%' OR currency_pair NOT LIKE '%AED%' OR currency_pair NOT LIKE '%OMR%' OR currency_pair NOT LIKE '%SAR%' OR currency_pair NOT LIKE '%QAR%' OR currency_pair NOT LIKE '%KWD%' OR currency_pair NOT LIKE '%BHD%' OR currency_pair NOT LIKE '%BGN%') AND IS_UK_GROUP = 'Y' AND vertical_system LIKE '%FXOPT%' AND RISK_CURRENCY_COMBINED NOT IN ('CNY', 'CNH', 'HKD', 'AED', 'OMR', 'SAR', 'QAR', 'KWD', 'BHD' ,'BGN', 'UNDEFINED') AND PRODUCT_TYPE_CODE NOT IN ('FXBSKT') AND is_basketbook = 'N' AND CCC_DIVISION NOT IN ('FID DVA','FIC DVA') AND CCC_STRATEGY NOT IN ('MS DVA STR NOTES IED') GROUP BY CCC_BUSINESS_AREA, CCC_PRODUCT_LINE, CCC_DIVISION, CURRENCY_PAIR ) abc