SELECT A.COB_DATE, CASE WHEN A.ASSET_TYPE_POS_DEF LIKE 'ZZCORR%' THEN 'ZZCORRs' ELSE A.ASSET_TYPE_POS_DEF END AS ASSET_TYPE_POS_DEF FROM cdwuser.U_CM_VIEW A WHERE A.BENCHMARK = 'MISSING' AND A.COB_DATE >= '09-01-2016' AND (A.USD_CM_DELTA <> 0 OR A.USD_CM_GAMMA <> 0 OR A.USD_CM_KAPPA <> 0 OR A.USD_IR_RHO <> 0 OR A.USD_CM_LEASE_RATE <> 0) group by A.COB_DATE, CASE WHEN A.ASSET_TYPE_POS_DEF LIKE 'ZZCORR%' THEN 'ZZCORRs' ELSE A.ASSET_TYPE_POS_DEF END