SELECT CASE WHEN MAJOR_EM IN ('China') THEN 'EM' ELSE MAJOR_EM END AS MAJOR_EM, CASE WHEN Substr(CURRENCY_OF_MEASURE,1,3) IN ('OMR','BHD','QAR','KWD','SAR','AED','BGN') THEN 'OTHER_PEGGED' ELSE Substr(CURRENCY_OF_MEASURE,1,3) END AS Currency, CASE WHEN TERM_NEW <= 1 THEN '0-1y' WHEN TERM_NEW > 1 AND TERM_NEW <= 5 THEN '2-5y' WHEN TERM_NEW > 5 AND TERM_NEW <= 10 THEN '7-10y' WHEN TERM_NEW > 10 THEN '10Y+' END AS TERM_NEW2, SUM(a.USD_IR_UNIFIED_PV01) as PV01, CASE WHEN Substr(CURRENCY_OF_MEASURE,1,3) IN ('CNH','CNY') THEN 'Y' ELSE IS_PEGGED END AS IS_PEGGED FROM cdwuser.U_DM_FX a WHERE a.cob_date IN ('2018-02-28') AND IS_UK_GROUP = 'Y' AND a.USD_IR_UNIFIED_PV01<>0 AND CCC_DIVISION NOT IN ('FID DVA','FIC DVA') AND CCC_STRATEGY NOT IN ('MS DVA STR NOTES IED') GROUP BY CASE WHEN MAJOR_EM IN ('China') THEN 'EM' ELSE MAJOR_EM END, CASE WHEN Substr(CURRENCY_OF_MEASURE,1,3) IN ('OMR','BHD','QAR','KWD','SAR','AED','BGN') THEN 'OTHER_PEGGED' ELSE Substr(CURRENCY_OF_MEASURE,1,3) END, CASE WHEN TERM_NEW <= 1 THEN '0-1y' WHEN TERM_NEW > 1 AND TERM_NEW <= 5 THEN '2-5y' WHEN TERM_NEW > 5 AND TERM_NEW <= 10 THEN '7-10y' WHEN TERM_NEW > 10 THEN '10Y+' END, CASE WHEN Substr(CURRENCY_OF_MEASURE,1,3) IN ('CNH','CNY') THEN 'Y' ELSE IS_PEGGED END