SELECT POSITION_ULT_ISSUER_PARTY_DARWIN_NAME, /* SUM (CASE WHEN (a.CCC_BUSINESS_AREA IN ('CPM', 'CPM TRADING (MPE)', 'CREDIT') OR a.CCC_STRATEGY IN ('MS CVA MPE - DERIVATIVES', 'MS CVA MNE - DERIVATIVES', 'EQ XVA HEDGING')) THEN 0 ELSE a.USD_EXPOSURE END) AS USD_EXPOSUREUKxCVA, */ SUM (a.USD_EXPOSURE) AS USD_EXPOSUREUK FROM cdwuser.U_EXP_MSR A WHERE a.cob_date IN ('2018-02-28') AND a.LE_GROUP = 'UK' AND A.ISSUER_COUNTRY_CODE IN ('XS', 'VGB', 'USA', 'SWE', 'PRT', 'NZL', 'NOR', 'NLD', 'LUX', 'JPN', 'JEY', 'ITA', 'ISL', 'IRL', 'IRL', 'IMN', 'GRC', 'GGY', 'GBR', 'FRA', 'FIN', 'ESP', 'DNK', 'DEU', 'CYP', 'CYM', 'CHE', 'CAN', 'BMU', 'BEL', 'AUT', 'AUS') AND A.CCC_PRODUCT_LINE = 'DISTRESSED TRADING' AND USD_EXPOSURE IS NOT NULL AND A.FID1_SENIORITY NOT IN ('AT1', 'SUBT1', 'SUBUT2') AND CCC_DIVISION NOT IN ('FID DVA','FIC DVA') AND CCC_STRATEGY NOT IN ('MS DVA STR NOTES IED') GROUP BY POSITION_ULT_ISSUER_PARTY_DARWIN_NAME