SELECT abc.CCC_BANKING_TRADING_LOCALREG, CASE WHEN CCC_Division IN ('BANK RESOURCE MANAGEMENT','FIXED INCOME DIVISION','NON CORE') THEN 'FID+BRM' ELSE CCC_Division END AS CCC_Division, SUM(DELTA) AS DELTA, SUM(ABS_Delta) AS GROSS_DELTA FROM ( SELECT CCC_BANKING_TRADING_LOCALREG, d.ISSUE_ID_DECOMP, d.CCC_Division, SUM(COALESCE(d.USD_EQ_DELTA_DECOMP,0)) AS Delta, ABS(SUM(COALESCE(d.USD_EQ_DELTA_DECOMP,0))) AS ABS_Delta FROM cdwuser.U_DECOMP_MSR d WHERE d.cob_date IN ('2018-02-28') AND d.CCC_BUSINESS_AREA NOT IN ('LENDING') AND d.LE_GROUP = 'UK' AND (d.CASH_ISSUE_TYPE <>'COMM' OR d.CASH_ISSUE_TYPE IS NULL) AND d.USD_EQ_DELTA_DECOMP <>0 AND CCC_DIVISION NOT IN ('FID DVA','FIC DVA') AND CCC_STRATEGY NOT IN ('MS DVA STR NOTES IED') GROUP BY d.COB_DATE, d.CCC_Division, d.ISSUE_ID_DECOMP, CCC_BANKING_TRADING_LOCALREG) abc GROUP BY abc.CCC_BANKING_TRADING_LOCALREG, CASE WHEN CCC_Division IN ('BANK RESOURCE MANAGEMENT','FIXED INCOME DIVISION','NON CORE') THEN 'FID+BRM' ELSE CCC_Division END