select a.cob_date ,sum(coalesce(a.USD_CM_DELTA,0)) as USD_CM_DELTA ,CASE WHEN a.CASH_ISSUE_TYPE = 'CMDT' THEN sum(coalesce(a.USD_EQ_DELTA_DECOMP,0)) END as ETP_USD_CM_DELTA From cdwuser.U_DM_EQ a where (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-27') and a.CCC_PL_REPORTING_REGION = 'EMEA' AND a.CCC_DIVISION='INSTITUTIONAL EQUITY DIVISION'  AND  a.CCC_BANKING_TRADING<>'BANKING'  Group by a.cob_date, a.CASH_ISSUE_TYPE