SELECT a.cob_date, a.CCC_PL_REPORTING_REGION, sum(coalesce(a.USD_EQ_DISC_RISK, 0)) AS div01 FROM cdwuser.U_DM_EQ a WHERE (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-21') and a.CCC_PL_REPORTING_REGION = 'EMEA' AND  a.CCC_BANKING_TRADING = 'TRADING' AND a.DIVISION = 'IED' group by cob_date, a.CCC_PL_REPORTING_REGION