SELECT CUR.COB_DATE, CUR.POSITION_ISSUER_PARTY_DARWIN_NAME AS DARWIN, CUR.USD_EXPOSURE, ABS(CUR.USD_EXPOSURE) AS ABS_USD_EXPOSURE, CUR.MRD_RATING, CUR.INDUSTRY, (COALESCE(CUR.USD_EXPOSURE,0) - COALESCE(PREV.USD_EXPOSURE,0)) AS DIFF_EXPOSURE, CUR.RANK AS RANKER FROM cdwuser.U_DM_CONC CUR LEFT OUTER JOIN cdwuser.U_DM_CONC PREV ON        CUR.RANK_TYPE = PREV.RANK_TYPE AND CUR.RANK_NAME = PREV.RANK_NAME AND CUR.POSITION_ISSUER_PARTY_DARWIN_NAME = PREV.POSITION_ISSUER_PARTY_DARWIN_NAME AND CUR.MRD_RATING = PREV.MRD_RATING AND CUR.INDUSTRY = PREV.INDUSTRY AND PREV.RANK_TYPE = 'EXPOSURE' AND (PREV.COB_DATE = '2018-02-27')  WHERE CUR.RANK_TYPE = 'EXPOSURE' AND (CUR.COB_DATE = '2018-02-28')  ORDER BY CUR.RANK DESC