select a.cob_date,a.CCC_PL_REPORTING_REGION, a.CCC_BUSINESS_AREA, sum(a.USD_EQ_KAPPA_PLS100BP_TIMEADJ) as TA_Kappa From cdwuser.U_DM_EQ a where (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-21') and a.IS_UK_GROUP = 'Y' AND  a.division = 'IED'   AND  a.CCC_BANKING_TRADING<>'BANKING'  Group by a.cob_date,a.CCC_PL_REPORTING_REGION, a.CCC_BUSINESS_AREA