select cob_date, a.CURRENCY_PAIR, sum(coalesce(a.USD_FX_KAPPA,0)) as fx_kappa, abs(sum(coalesce(a.USD_FX_KAPPA,0))) as abs_fx_kappa from cdwuser.u_dm_FX a  WHERE (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-21') and a.IS_UK_GROUP = 'Y' AND a.ccc_division in ('INSTITUTIONAL EQUITY DIVISION') AND A.CCC_BANKING_TRADING_LOCALREG IN ('TRADING') and coalesce(a.usd_fx_kappa,0)<>0 and a.currency_of_measure is not null group by cob_date, a.CURRENCY_PAIR order by abs(sum(coalesce(a.usd_fx,0))) desc