select COB_DATE, LIMIT_ID, LIMIT_NAME, LIMIT_APPLIED_TO, PARENT_DESCRIPTION, CASE WHEN TEMP_LIMIT_VALUE IS NULL THEN LIMIT_VALUE WHEN (TEMP_LIMIT_VALUE IS NOT NULL AND TEMP_LIMIT_EXPIRATION_DATE > COB_DATE) THEN TEMP_LIMIT_VALUE ELSE LIMIT_VALUE END AS LIMIT_AMOUNT, SUM_OF FROM Cdwuser.U_FLOW_LIMITS where COB_DATE in ( '2018-02-27', '2018-02-28' )AND ( (LIMIT_ID IN ('3') AND PARENT_DESCRIPTION in ('GWM')) OR LIMIT_ID IN ('5308', '7490','7675') )