select b.COB_DATE, b.BUSINESS, b.MODEL, b.USD_MV, b.USD_PV01 from ( select A.COB_DATE, case when A.CCC_TAPS_COMPANY = '1633' then 'MSBNA' when A.CCC_TAPS_COMPANY = '6635' then 'MSPBNA' else 'OTHER WM' end as BUSINESS, A.VERTICAL_SYSTEM||' : '||A.PAYOFF_MODEL||' : '||A.MARKET_MODEL as MODEL, sum(A.USD_MARKET_VALUE) as USD_MV, 0 as USD_PV01 from Cdwuser.U_OT_MSR a where a.COB_DATE = '2018-02-28' and (a.CCC_TAPS_COMPANY in ('1633', '6635') or a.CCC_HIERARCHY_LEVEL2 = 'WEALTH MANAGEMENT') and a.USD_MARKET_VALUE is not null group by A.COB_DATE, case when A.CCC_TAPS_COMPANY = '1633' then 'MSBNA' when A.CCC_TAPS_COMPANY = '6635' then 'MSPBNA' else 'OTHER WM' end, A.VERTICAL_SYSTEM||' : '||A.PAYOFF_MODEL||' : '||A.MARKET_MODEL union all select A.COB_DATE, case when A.CCC_TAPS_COMPANY = '1633' then 'MSBNA' when A.CCC_TAPS_COMPANY = '6635' then 'MSPBNA' else 'OTHER WM' end as BUSINESS, A.VERTICAL_SYSTEM||' : '||A.PAYOFF_MODEL||' : '||A.MARKET_MODEL as MODEL, 0 as USD_MV, sum(A.USD_IR_UNIFIED_PV01) as USD_PV01 from Cdwuser.U_IR_MSR a where a.COB_DATE = '2018-02-28' and (a.CCC_TAPS_COMPANY in ('1633', '6635') or a.CCC_HIERARCHY_LEVEL2 = 'WEALTH MANAGEMENT') and a.USD_IR_UNIFIED_PV01 is not null group by A.COB_DATE, case when A.CCC_TAPS_COMPANY = '1633' then 'MSBNA' when A.CCC_TAPS_COMPANY = '6635' then 'MSPBNA' else 'OTHER WM' end, A.VERTICAL_SYSTEM||' : '||A.PAYOFF_MODEL||' : '||A.MARKET_MODEL ) b