SELECT A.COB_DATE ,a.POSITION_ISSUER_PARTY_DARWIN_NAME ,CASE WHEN A.TERM_OF_MEASURE / 365 < 3 THEN '1-3yr' WHEN A.TERM_OF_MEASURE / 365 >= 3 AND A.TERM_OF_MEASURE / 365 < 7 THEN '3-7yr' WHEN A.TERM_OF_MEASURE / 365 >= 7 AND A.TERM_OF_MEASURE / 365 < 12 THEN '7-12yr' WHEN A.TERM_OF_MEASURE / 365 >= 12 AND A.TERM_OF_MEASURE / 365 < 20 THEN '12-20yr' WHEN A.TERM_OF_MEASURE / 365 >= 20 THEN '20yr' ELSE 'other' END AS TERM ,CASE WHEN a.CCC_DIVISION IN ('FIXED INCOME DIVISION','INSTITUTIONAL EQUITY DIVISION') THEN a.CCC_DIVISION ELSE 'OTHER' END AS CCC_DIVISION ,CASE WHEN CCC_TAPS_COMPANY IN ('7325','5972') THEN 'Caieiras Fundo de Investimento (5972, 7325)' WHEN CCC_TAPS_COMPANY IN ('4171' ,'0921','6411') THEN 'Banco Morgan Stanley S.A. (0921, 4171, 6411)' WHEN CCC_TAPS_COMPANY = '0925' THEN 'Morgan Stanley Corretora de Titulos e Valores Mobiliarios (0925)' WHEN CCC_TAPS_COMPANY IN ('8890','8840' ,'4171','0921' ,'0925','5972','7325','6411') THEN CCC_TAPS_COMPANY ELSE 'OTHERS' END AS LEGAL_ENTITY ,CASE WHEN A.PRODUCT_TYPE_CODE IN ('CAP' ,'FLOOR','SWAPTION') THEN 'OPTION' ELSE A.PRODUCT_TYPE_CODE END AS PRODUCT_TYPE_CODE ,sum(a.USD_IR_UNIFIED_PV01) AS PV01 FROM dwuser.U_IR_MSR a WHERE cob_date >= '11/30/2017' AND cob_date <= '02/28/2018' AND A.CCC_TAPS_COMPANY IN ('8890','8840') AND a.VAR_EXCL_FL = 'N' AND A.USD_IR_UNIFIED_PV01 IS NOT NULL GROUP BY a.CCC_DIVISION ,A.CCC_TAPS_COMPANY ,A.COB_DATE ,A.TERM_OF_MEASURE ,a.POSITION_ISSUER_PARTY_DARWIN_NAME ,CASE WHEN A.PRODUCT_TYPE_CODE IN ('CAP' ,'FLOOR','SWAPTION') THEN 'OPTION' ELSE A.PRODUCT_TYPE_CODE END