SELECT a.COB_DATE, a.CCC_PL_REPORTING_REGION, a.CCC_PRODUCT_LINE, a.BOOK, CASE WHEN (a.GICS_LEVEL_1_NAME = 'FINANCIALS' and book not in ('IGFIN') AND a.POSITION_ULT_ISSUER_PARTY_DARWIN_NAME NOT IN ('GAS NATURAL SDG, S.A.','AT SECURITIES B.V.')) THEN 'FINANCIALS' WHEN (a.GICS_LEVEL_1_NAME = 'FINANCIALS' and book in ('IGFIN')) THEN 'EXCL' ELSE 'CORPORATES' END AS FLAG_SECTOR, a.POSITION_ULT_ISSUER_PARTY_DARWIN_NAME, a.FID1_SENIORITY, a.TAPSCUSIP, sum(coalesce(a.USD_EXPOSURE,0))/1000 as NET_EXPOSURE, abs(sum(coalesce(a.USD_EXPOSURE,0)))/1000 as GROSS_EXPOSURE FROM cdwuser.U_EXP_MSR a WHERE a.COB_DATE >= '2018-02-21' AND a.CCC_DIVISION = 'FIXED INCOME DIVISION' AND a.CCC_BANKING_TRADING = 'TRADING' AND a.CCC_PRODUCT_LINE NOT IN ('DISTRESSED TRADING') AND a.PRODUCT_TYPE_CODE IN ('BOND','BONDFUT','BONDFUTOPT','BONDIL','BONDOPT','PREF') AND a.FID1_SENIORITY IN ('AT1','SUBT1','SUBUT2') AND a.USD_EXPOSURE <> 0 AND a.CCC_TAPS_COMPANY = '0302' GROUP BY a.COB_DATE, a.CCC_PL_REPORTING_REGION, a.CCC_PRODUCT_LINE, a.BOOK, CASE WHEN (a.GICS_LEVEL_1_NAME = 'FINANCIALS' and book not in ('IGFIN') AND a.POSITION_ULT_ISSUER_PARTY_DARWIN_NAME NOT IN ('GAS NATURAL SDG, S.A.','AT SECURITIES B.V.')) THEN 'FINANCIALS' WHEN (a.GICS_LEVEL_1_NAME = 'FINANCIALS' and book in ('IGFIN')) THEN 'EXCL' ELSE 'CORPORATES' END, a.POSITION_ULT_ISSUER_PARTY_DARWIN_NAME, a.FID1_SENIORITY, a.TAPSCUSIP