SELECT a.COB_DATE, sum(a.USD_CM_Delta) as Dollar_Delta, sum(cast(RAW_CM_DELTA as numeric(15,5))) as Raw_Delta, sum(cast(RAW_CM_KAPPA as numeric(15,5))) as Raw_Vega, a.TIME_BUCKET_CALENDAR, a.CCC_PRODUCT_LINE, a.ccc_strategy, CASE WHEN A.PRODUCT_SUB_TYPE_CODE IN ('N EAST CONSUMPTI', 'N EAST CONSUMPTION') THEN 'N EAST CONSUMPTI' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('N EAST APPALACHI', 'N EAST APPALACHIA') THEN 'N EAST APPALACHI' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('POLYPROPELENE HO', 'POLYPROPELENE HOMOPOLYMER') THEN 'POLYPROPELENE HO' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('POLYPROPYLENE CO', 'POLYPROPYLENE COPOLYMER') THEN 'POLYPROPYLENE CO' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('PURE TEREPHTHALI', 'PURE TEREPHTHALIC ACID') THEN 'PURE TEREPHTHALI' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('GSCI-PRECIOUS ME', 'GSCI-PRECIOUS METALS') THEN 'GSCI-PRECIOUS ME' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('ZERO PRICED EXPO', 'ZERO PRICED EXPOSURE') THEN 'ZERO PRICED EXPO' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('DJUBS-BASE METAL', 'DJUBS-BASE METALS') THEN 'DJUBS-BASE METAL' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('GAS SULFUR CREDI', 'GAS SULFUR CREDIT') THEN 'GAS SULFUR CREDI' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('GASOIL TIMESPREA', 'GASOIL TIMESPREAD') THEN 'GASOIL TIMESPREA' WHEN A.PRODUCT_SUB_TYPE_CODE IN ('USD INTEREST RAT', 'USD INTEREST RATE') THEN 'USD INTEREST RAT' ELSE A.PRODUCT_SUB_TYPE_CODE END AS PRODUCT_SUB_TYPE_CODE, a.PRODUCT_TYPE_CODE, case when (a.PRODUCT_TYPE_CODE like 'TEXAS%' or a.PRODUCT_TYPE_CODE like '%ERCOT%') then 'Texas' when a.PRODUCT_TYPE_CODE like 'WEST%' then 'West' when (a.PRODUCT_TYPE_CODE like ('EAST%') or a.PRODUCT_TYPE_CODE like ('MIDWEST%') or a.PRODUCT_TYPE_CODE like ('GRNPWR%')) then 'East' when a.PRODUCT_TYPE_CODE in ('COAL', 'COAL-US', 'EMISSIONS-US') then 'Other Fuels' when a.PRODUCT_TYPE_CODE in ('NATGAS') then 'Natgas' else 'other' end as region FROM cdwuser.U_CM_MSR a WHERE A.COB_DATE IN ('2018-02-28','2018-02-27') AND (CCC_PL_REPORTING_REGION IN ('EUROPE','EMEA')) AND ((CCC_DIVISION='COMMODITIES' AND CCC_BUSINESS_AREA = 'NA ELECTRICITYNATURAL GAS') /*OLD LOGIC*/ OR (A.CCC_DIVISION = 'FIXED INCOME DIVISION' AND A.CCC_BUSINESS_AREA = 'COMMODITIES' and a.CCC_PRODUCT_LINE IN ('NA POWER & GAS'))) /*NEW LOGIC*/ AND PRODUCT_TYPE_CODE NOT IN ('CURRENCY', 'INTEREST RATE', 'INFLATION', 'TBD', 'MISC','CVA', 'FVA', 'ERROR') and NOT(INCLUDE_IN_REG_CAAP_FL = 'N' and PRODUCT_SUB_TYPE_CODE in ('N EAST CONSUMPTI', 'N EAST CONSUMPTION', 'N EAST APPALACHI', 'N EAST APPALACHIA') and BOOK = '18003') group by a.COB_DATE, a.PRODUCT_TYPE_CODE, a.TIME_BUCKET_CALENDAR, a.FUTURES_EQUIVALENT_DATE, a.TIME_BUCKET_QUARTER, a.CCC_PRODUCT_LINE, a.CCC_STRATEGY,a.PRODUCT_SUB_TYPE_CODE