Select A.BREAKDOWN, A.CCC_PL_REPORTING_REGION, A.TIME_BUCKET_CALENDAR, A.QUARTERS, A.COB_DATE, SUM(A.DOLLAR_DELTA) as DOLLAR_DELTA, SUM(A.RAW_VEGA) as RAW_VEGA FROM( Select COB_date,CCC_PL_REPORTING_REGION, CCC_BUSINESS_AREA, SUM(cast(USD_CM_DELTA as numeric(15,5))) as DOLLAR_DELTA, SUM (CASE WHEN a.PRODUCT_TYPE_CODE = 'TIMESPREAD' AND a.PROD_POS_NAME_DESCRIPTION = 'WCS POST SPREAD' THEN 0 WHEN (a.book = 'COMXO' and A.CCC_PRODUCT_LINE IN ('COMMOD EXOTICS')) then CAST(USD_CM_KAPPA_ABSOLUTE AS NUMERIC (15, 5)) ELSE CAST (a.RAW_CM_KAPPA AS NUMERIC (15, 5)) END) AS RAW_VEGA,
 TIME_BUCKET_CALENDAR, EXPIRATION_DATE,TIME_BUCKET_QUARTER, CCC_TRD_BOOK, case when CCC_TRD_BOOK in ('MAN SPEC', 'NA AUTOMATED TRADING','NA SPECULATIVE TRADING', 'NA OPTIONS SPEC','MACRO HEDGE') then 'Index+ Non-Linear' when CCC_TRD_BOOK in ( 'NA HYBRID INVESTOR OPT' ) then 'Hybrid Investor Options' when CCC_TRD_BOOK in ('NA TRADING','EXOTICS-OTHER','NA IB COLLATERAL CHARGES') then 'Index+ Linear' when CCC_TRD_BOOK in ('AGS WASH', 'CZARNIKOW', 'GLOBAL GRAINS', 'GLOBAL SOFTS','SOFT','SOFTS','GLOBAL VEG OIL','GRAIN') then 'Agriculturals' else 'Other' end as BREAKDOWN, case when EXPIRATION_DATE < ('2019-09-01') then time_bucket_quarter end as quarters FROM cdwuser.U_CM_MSR a Where COB_DATE IN ('2018-02-28','2018-02-27') AND ((CCC_BUSINESS_AREA = 'INVESTOR BUSINESS' AND CCC_DIVISION IN ('COMMODITIES')) /*OLD LOGIC*/ OR (CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA = 'COMMODITIES' and CCC_PRODUCT_LINE IN ('COMMOD INDEX', 'COMMOD EXOTICS'))) /*NEW LOGIC*/ Group by COB_date,CCC_PL_REPORTING_REGION, CCC_BUSINESS_AREA, TIME_BUCKET_CALENDAR, EXPIRATION_DATE, TIME_BUCKET_QUARTER, CCC_TRD_BOOK)A GROUP BY A.BREAKDOWN, A.CCC_PL_REPORTING_REGION, A.TIME_BUCKET_CALENDAR, A.COB_DATE, A.QUARTERS