SELECT     a.COB_DATE,     a.ISSUER_COUNTRY_CODE,     CASE WHEN a.PRODUCT_TYPE_CODE LIKE 'CMBS%' THEN 'CMBS' WHEN a.PRODUCT_TYPE_CODE LIKE 'RMBS%' THEN 'RMBS'          /* WHEN a.PRODUCT_TYPE_CODE in ('DEFSWAP','CRDINDEX') THEN 'HEDGES' */     ELSE 'OTHERS' END AS POSTYPE,     SUM(CAST(a.USD_EXPOSURE AS DOUBLE PRECISION)) AS SUM_USD_EXPOSURE FROM cdwuser.U_CR_MSR_INTRPLT a WHERE     1 = 1 AND (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-27') and A.CCC_PL_REPORTING_REGION in ('ASIA PACIFIC') AND        CCC_BANKING_TRADING='TRADING' AND     a.CCC_DIVISION IN ('FIXED INCOME DIVISION') AND     a.CCC_BUSINESS_AREA = 'SECURITIZED PRODUCTS GRP' GROUP BY     a.COB_DATE,     a.ISSUER_COUNTRY_CODE,     CASE WHEN a.PRODUCT_TYPE_CODE LIKE 'CMBS%' THEN 'CMBS' WHEN a.PRODUCT_TYPE_CODE LIKE 'RMBS%' THEN 'RMBS'          /* WHEN a.PRODUCT_TYPE_CODE in ('DEFSWAP','CRDINDEX') THEN 'HEDGES' */     ELSE 'OTHERS' END