SELECT     a.COB_DATE,     CASE         WHEN a.CR_ULTIMATE_CNTRY_CODE <> 'UNDEFINED' THEN a.CR_ULTIMATE_CNTRY_CODE         WHEN a.COUNTRY_CD_OF_RISK <> 'UNDEFINED' THEN a.COUNTRY_CD_OF_RISK         WHEN a.PRODUCT_TYPE_CODE = 'CONVRT' THEN a.ISSUER_COUNTRY_CODE         WHEN a.CR_ULTIMATE_CNTRY_CODE = 'UNDEFINED' THEN a.ISSUER_COUNTRY_CODE     ELSE a.CR_ULTIMATE_CNTRY_CODE END AS ULT_COUNTRY,     CASE         WHEN a.CCC_PRODUCT_LINE= 'DISTRESSED TRADING' THEN 'Distressed'      ELSE 'Others' END AS SSG_POS_GRP,     SUM(a.USD_EXPOSURE) AS USD_ASSET_EXPOSURE FROM     cdwuser.U_CR_MSR a WHERE     1 = 1 AND (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-27') and A.CCC_PL_REPORTING_REGION in ('ASIA PACIFIC') AND      ((a.CCC_DIVISION = 'FIXED INCOME DIVISION' AND     a.CCC_BUSINESS_AREA = 'CREDIT-CORPORATES' AND     a.CCC_PRODUCT_LINE = 'DISTRESSED TRADING') OR     a.CCC_DIVISION='INSTITUTIONAL SECURITIES OTHER') AND     a.VAR_EXCL_FL <> 'Y' GROUP BY     a.COB_DATE,     CASE         WHEN a.CCC_PRODUCT_LINE= 'DISTRESSED TRADING' THEN 'Distressed'      ELSE 'Others' END,     CASE         WHEN a.CR_ULTIMATE_CNTRY_CODE <> 'UNDEFINED' THEN a.CR_ULTIMATE_CNTRY_CODE         WHEN a.COUNTRY_CD_OF_RISK <> 'UNDEFINED' THEN a.COUNTRY_CD_OF_RISK         WHEN a.PRODUCT_TYPE_CODE = 'CONVRT' THEN a.ISSUER_COUNTRY_CODE         WHEN a.CR_ULTIMATE_CNTRY_CODE = 'UNDEFINED' THEN a.ISSUER_COUNTRY_CODE     ELSE a.CR_ULTIMATE_CNTRY_CODE END