Select a.COB_DATE, a.CCC_PL_REPORTING_REGION, a.PROD_POS_NAME_DESCRIPTION, sum(a.raw_greek) as raw_greek, case when a.DAYS2exp >=1980 then '5+yrs' when (a.DAYS2exp <1980 and a.DAYS2exp >=1440)then '5yrs' when (a.DAYS2exp <1440 and a.DAYS2exp >=1080) then '4yrs' when (a.DAYS2exp <1080 and a.DAYS2exp >=720)then '3yrs' when (a.DAYS2exp <720 and a.DAYS2exp >=360) then '2yrs'when (a.DAYS2exp <360 and a.DAYS2exp >=270)then '1yr' when (a.DAYS2exp <270 and a.DAYS2exp >=180) then '9m'when (a.DAYS2exp <180 and a.DAYS2exp >=90)then '6m'when (a.DAYS2exp <90 and a.DAYS2exp >=60) then '3m' when (a.DAYS2exp <60 and a.DAYS2exp >=30)then '2m' when (a.DAYS2exp <30 and a.DAYS2exp >=7.5) then '1m'when (a.DAYS2exp <7.5 and a.DAYS2exp >=1.5)then '1wk'when a.DAYS2exp < 1.5 then'1day' else 'Check' end as MetalsTerm from ( SELECT COB_DATE,CCC_PL_REPORTING_REGION, PRODUCT_TYPE_CODE,PROD_POS_NAME_DESCRIPTION, (sum(USD_CM_LEASE_RATE)::numeric(30,10))/100 as raw_greek, EXPIRATION_DATE, (extract(YEAR from (EXPIRATION_DATE))-extract (YEAR from (COB_DATE)))*360+(extract (MONTH from (EXPIRATION_DATE))-extract(MONTH from (COB_DATE)))*30+(extract(DAY from (EXPIRATION_DATE))-extract(DAY from (COB_DATE))) as DAYS2exp, TIME_BUCKET_CALENDAR FROM cdwuser.U_CM_MSR Where COB_DATE IN ('2018-02-28','2018-01-31') AND ((CCC_DIVISION='COMMODITIES' AND CCC_BUSINESS_AREA not in ('CREDIT','MS CVA MNE - COMMOD', 'COMMODS FINANCING','INVESTMENTS AND STR TRANS','INVESTOR BUSINESS')) OR(CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA = 'COMMODITIES' AND CCC_PRODUCT_LINE NOT IN ('COMMOD LENDING', 'COMMOD EXOTICS', 'COMMOD INDEX') AND CCC_STRATEGY NOT IN ('MS CVA MNE - COMMOD', 'CMD LEGACY LAONS & CLAIMS', 'CVA RISK MANAGEMENT', 'FVA RISK MANAGEMENT', 'FUNDING COLLATERAL COMM'))) /* NEW LOGIC*/ and PRODUCT_TYPE_CODE in ('CURRENCY','INV STR TRANS - METALS') AND (CCC_PL_REPORTING_REGION IN ('EUROPE','EMEA')) group by COB_DATE,CCC_PL_REPORTING_REGION, PRODUCT_TYPE_CODE, TIME_BUCKET_CALENDAR, EXPIRATION_DATE,PROD_POS_NAME_DESCRIPTION )A group by a.COB_DATE, a.CCC_PL_REPORTING_REGION, a.PROD_POS_NAME_DESCRIPTION, a.DAYS2exp