SELECT     COB_DATE,     SUM (USD_CM_Delta) AS USD_CM_Delta,     CASE         WHEN product_type_code IN ('DIST', 'JET') THEN 'Distillate'         WHEN product_type_code IN ('FUEL', 'GAS', 'CRUDE', 'NAPHTHA','ETHANOL', 'NGL') THEN product_type_code         WHEN product_type_code IN ('CLEAN FREIGHT', 'DIRTY FREIGHT') THEN 'Freight' END AS asset_type FROM CDWUSER.U_CM_MSR a WHERE (a.COB_DATE = '2018-02-28' or a.COB_DATE = '2018-02-21') and     CCC_BUSINESS_AREA = 'COMMODITIES' AND     CCC_DIVISION = 'FIXED INCOME DIVISION' AND     CCC_PRODUCT_LINE = 'OIL & PRODUCTS' AND     /*CCC_TRD_BOOK <> 'DEVELOPMENT' AND*/ A.CCC_PL_REPORTING_REGION in ('ASIA PACIFIC') AND      product_type_code NOT IN ('CLEAN FREIGHT', 'DIRTY FREIGHT') /*AND     (a.PRODUCT_TYPE_CODE IN ('BONDFUT', 'BONDFUTOPT') OR      (a.VAR_EXCL_FL <> 'Y' AND a.MEASURE_VAR_EXCL_FL <> 'Y'))*/ GROUP BY     COB_DATE,     CASE         WHEN product_type_code IN ('DIST', 'JET') THEN 'Distillate'         WHEN product_type_code IN ('FUEL', 'GAS', 'CRUDE', 'NAPHTHA','ETHANOL', 'NGL') THEN product_type_code         WHEN product_type_code IN ('CLEAN FREIGHT', 'DIRTY FREIGHT') THEN 'Freight' END