SELECT a.Cob_date, a.TIME_BUCKET_CALENDAR, a.PRODUCT_TYPE_CODE , 'OIL LIQUIDS' as CCC_BUSINESS_AREA, sum(cast(a.USD_CM_Delta as numeric(15,5))) as Dollar_Delta, sum(case when (a.PRODUCT_TYPE_CODE = 'TIMESPREAD' and a.PROD_POS_NAME_DESCRIPTION = 'WCS POST SPREAD' ) OR a.product_type_code in ('CLEAN FREIGHT','DIRTY FREIGHT','COAL','DRY FREIGHT','EMISSIONS-EU','IRON ORE','NATGAS','PLASTICS') then 0 else cast(a.RAW_CM_DELTA as numeric(15,5)) end) as raw_delta FROM cdwuser.U_CM_MSR a WHERE A.COB_DATE IN ('2018-02-28','2018-01-31') AND (CCC_PL_REPORTING_REGION IN ('EUROPE','EMEA')) AND ((CCC_BUSINESS_AREA NOT IN ('OIL LIQUIDS') AND CCC_DIVISION = 'COMMODITIES') OR (CCC_DIVISION = 'FIXED INCOME DIVISION' AND CCC_BUSINESS_AREA = 'COMMODITIES' and CCC_PRODUCT_LINE IN ('OIL & PRODUCTS'))) AND PRODUCT_TYPE_CODE NOT IN ('CURRENCY', 'INTEREST RATE', 'INFLATION', 'TBD', 'MISC','CVA', 'FVA', 'ERROR') GROUP BY a.Cob_date, a.TIME_BUCKET_CALENDAR, a.PRODUCT_TYPE_CODE